test('@container', async () => {
  expect(
    await run([
      '@container',
      '@container-normal',
      '@container/sidebar',
      '@container-normal/sidebar',
      '@container-size',
      '@container-size/sidebar',
    ]),
  ).toMatchInlineSnapshot(`
    ".\\@container-normal\\/sidebar {
      container: sidebar;
    }

    .\\@container-size\\/sidebar {
      container: sidebar / size;
    }

    .\\@container\\/sidebar {
      container: sidebar / inline-size;
    }

    .\\@container {
      container-type: inline-size;
    }

    .\\@container-normal {
      container-type: normal;
    }

    .\\@container-size {
      container-type: size;
    }"
  `)
  expect(
    await run([
      '-@container',
      '-@container-normal',
      '-@container/sidebar',
      '-@container-normal/sidebar',
      '-@container-size',
      '-@container-size/sidebar',
    ]),
  ).toEqual('')
})
